import React, { useState } from 'react';
import { Tabs, Tab, Container } from 'react-bootstrap';
import UserTable from './UserTable'; 
import ProductTable from './ProductTable'; // Componente de tabla de productos
import ProductForm from './ProductForm';   // Componente de formulario de productos

const AdminDashboard = () => {
    // Hook para controlar la vista: 'list' (mostrar tabla) o 'form' (mostrar formulario)
    const [view, setView] = useState('list'); 
    // Hook para guardar el producto que se está editando
    const [productToEdit, setProductToEdit] = useState(null); 

    // Función que se llama al hacer clic en "Editar" en la tabla
    const handleEditProduct = (product) => {
        setProductToEdit(product); // Carga los datos en el estado
        setView('form');           // Cambia la vista a formulario
    };

    // Función que regresa a la vista de lista y limpia el producto a editar
    const handleFormSubmit = () => {
        setProductToEdit(null);
        setView('list');
    };

    // Renderiza la lista o el formulario según el estado 'view'
    const renderProductView = () => {
        if (view === 'form') {
            return (
                <ProductForm 
                    initialData={productToEdit} 
                    onSubmit={handleFormSubmit} // Regresa a la lista
                    onCancel={handleFormSubmit}
                />
            );
        }
        return (
            <>
                <Button variant="primary" className="my-3" onClick={() => { setProductToEdit(null); setView('form'); }}>
                    + Crear Nuevo Producto
                </Button>
                <ProductTable onEdit={handleEditProduct} />
            </>
        );
    };
    
    return (
        <Container className="my-5">
            <h1 className="text-center">Panel de Administración</h1>

            <Tabs defaultActiveKey="productos" id="admin-tabs" className="mb-3">
                
                <Tab eventKey="productos" title="Gestión de Productos">
                    <h3 className="mt-3">Crear, Editar y Listar Productos</h3>
                    {renderProductView()} {/* Lógica condicional de Producto */}
                </Tab>
                
                <Tab eventKey="usuarios" title="Gestión de Usuarios">
                    <h3 className="mt-3">Gestión de Clientes y Roles</h3>
                    <UserTable /> 
                </Tab>

                <Tab eventKey="reportes" title="Reportes" disabled>
                    <p className="text-center my-4">Vista futura.</p>
                </Tab>
            </Tabs>
        </Container>
    );
};

export default AdminDashboard;
