import React from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';

const blogPosts = [
    { id: 1, title: '¡Nuevo grano de café rubio!', image: 'blog.png', summary: 'Hemos lanzado un nuevo café con un sabor especial, ideal para los amantes de los sabores brillantes.' },
    { id: 2, title: 'Consejos para disfrutar tu pastel', image: 'cake1.png', summary: 'Descubre la mejor manera de acompañar nuestros deliciosos pasteles para una experiencia inolvidable.' }
];

const BlogPage = () => {
    return (
        <Container className="my-5">
            <h1 className="text-center mb-5">Noticias Importantes (Blogs)</h1>
            <Row xs={1} md={2} className="g-4 justify-content-center">
                {blogPosts.map(post => (
                    <Col key={post.id} className="mb-3">
                        <Card className="h-100 blog-item">
                            <Row g={0}>
                                <Col md={4}>
                                    <Card.Img src={`/imagenes/${post.image}`} alt={post.title} style={{ height: '100%', objectFit: 'cover' }} />
                                </Col>
                                <Col md={8}>
                                    <Card.Body>
                                        <Card.Title>{post.title}</Card.Title>
                                        <Card.Text>{post.summary}</Card.Text>
                                        {/* Simulación de enlace a detalle */}
                                        <Button variant="outline-dark" size="sm" href={`#detalle-blog/${post.id}`}>Leer más</Button>
                                    </Card.Body>
                                </Col>
                            </Row>
                        </Card>
                    </Col>
                ))}
            </Row>
        </Container>
    );
};

export default BlogPage;
