import React from 'react';
import { Navbar, Container, Nav, Button } from 'react-bootstrap';
import '../App.css';  // Importa tus estilos existentes

const Layout = ({ children, setView = () => {} }) => {
    const getCartTotal = () => {
        try {
            const carrito = JSON.parse(localStorage.getItem('carrito')) || [];
            return carrito.reduce((sum, item) => sum + (Number(item.precio) || 0), 0)
                         .toLocaleString('es-CL');
        } catch {
            return '0';
        }
    };

    return (
        <>
            <div className="Superior">
                <Navbar expand="lg" className="sticky-top">
                    <Container>
                        <Navbar.Brand 
                            onClick={() => setView('home')} 
                            className="logo"
                            style={{ cursor: 'pointer' }}
                        >
                            Coffee Shop â˜•
                        </Navbar.Brand>
                        <Navbar.Toggle aria-controls="basic-navbar-nav" />
                        <Navbar.Collapse id="basic-navbar-nav">
                            <Nav className="me-auto elementos">
                                <Nav.Link onClick={() => setView('home')}>Home</Nav.Link>
                                <Nav.Link onClick={() => setView('registro')}>Registro</Nav.Link>
                                <Nav.Link onClick={() => setView('admin')}>Admin</Nav.Link>
                            </Nav>
                            <Button 
                                id="boton-pagar"
                                onClick={() => setView('carrito')}
                            >
                                ðŸ›’ ${getCartTotal()}
                            </Button>
                        </Navbar.Collapse>
                    </Container>
                </Navbar>
            </div>

            <Container className="main-content">
                {children}
            </Container>

            <footer className="py-3 mt-5">
                <Container>
                    <p className="mb-0">
                        &copy; {new Date().getFullYear()} Coffee Shop
                    </p>
                </Container>
            </footer>
        </>
    );
};

export default Layout;
