import React from 'react';
import { Navbar, Container, Nav, Button } from 'react-bootstrap';
import '../App.css'; 

// Componente Layout: Provee la estructura base (Navbar y Footer) y la navegaci贸n
const Layout = ({ children, setView = () => {} }) => {

    // Funci贸n para obtener el total del carrito (usando LocalStorage)
    const getCartTotal = () => {
        try {
            const carrito = JSON.parse(localStorage.getItem('carrito')) || [];
            // Suma el precio y formatea a moneda chilena
            return carrito.reduce((sum, item) => sum + (Number(item.precio) || 0), 0)
                         .toLocaleString('es-CL');
        } catch {
            return '0'; // Devuelve 0 en caso de error de parsing de LocalStorage
        }
    };

    return (
        <>
            {/* Navbar Principal (Usando el color #3E2723 de tu clase .Superior) */}
            <Navbar expand="lg" className="sticky-top" style={{ backgroundColor: '#3E2723' }} variant="dark">
                <Container>
                    <Navbar.Brand 
                        onClick={() => setView('home')} 
                        style={{ cursor: 'pointer', color: 'white', fontWeight: 'bold' }}
                    >
                        Coffee Shop 
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    
                    <Navbar.Collapse id="basic-navbar-nav">
                        
                        {/* 1. Enlaces Principales (Home, Admin) */}
                        <Nav className="me-auto">
                            <Nav.Link onClick={() => setView('home')}>Home</Nav.Link>
                            <Nav.Link onClick={() => setView('registro')}>Registro</Nav.Link>
                            <Nav.Link onClick={() => setView('admin')}>Admin</Nav.Link>
                            {/* Puedes a帽adir m谩s enlaces aqu铆: Nosotros, Contacto, Blogs */}
                            <Nav.Link onClick={() => setView('contacto')}>Contacto</Nav.Link>
                        </Nav>

                        {/* 2. Bot贸n del Carrito */}
                        <Button 
                            variant="success"
                            onClick={() => setView('carrito')}
                            className="ms-3"
                            style={{ backgroundColor: '#4CAF50', borderColor: '#4CAF50' }}
                        >
                             Carrito ${getCartTotal()}
                        </Button>

                        {/* 3. Botones de Login/Crear Cuenta */}
                        <Nav>
                            <Button variant="outline-light" onClick={() => setView('login')} className="ms-3">
                                Iniciar Sesi贸n
                            </Button>
                            <Button variant="info" onClick={() => setView('registro')} className="ms-2">
                                Crear Cuenta
                            </Button>
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>

            {/* Contenedor principal donde se inyecta la vista actual */}
            <Container className="main-content" style={{ minHeight: '80vh' }}>
                {children} 
            </Container>

            {/* Footer */}
            <footer className="bg-dark text-white text-center py-3 mt-5">
                <Container>
                    <p className="mb-0">
                        &copy; {new Date().getFullYear()} Coffee Shop. Evaluaci贸n React.
                    </p>
                </Container>
            </footer>
        </>
    );
};

export default Layout;
