// src/App.js (Versión Final)

import React, { useState } from 'react';
import Layout from "./components/Layout";
import ProductList from "./components/ProductList"; 
import RegistroForm from "./components/RegistroForm"; 
import AdminDashboard from "./components/AdminDashboard";
import Cart from "./components/Cart";
import LoginForm from "./components/LoginForm";
import AboutUs from "./components/AboutUs"; // << NUEVO
import BlogPage from "./components/BlogPage"; // << NUEVO
import './App.css'; // Asegúrate de tener los estilos

function App() {
  const [currentView, setCurrentView] = useState('home'); 

  const renderView = () => {
    switch (currentView) {
      case 'home':
        return <ProductList />;
      case 'registro':
        return <RegistroForm />;
      case 'login':
        return <LoginForm setView={setCurrentView} />;
      case 'carrito':
        return <Cart />;
      case 'admin':
        return <AdminDashboard />;
      case 'contacto':
        return <ContactPage setView={setCurrentView} />; // << NUEVO
      case 'nosotros':
        return <AboutUs />; // << NUEVO
      case 'blogs':
        return <BlogPage />; // << NUEVO
      default:
        return <ProductList />;
    }
  };

  return (
    <Layout setView={setCurrentView}>
      {renderView()}
    </Layout>
  );
}

export default App;
