import React from 'react';
import { Navbar, Container, Nav, Button } from 'react-bootstrap';

// Este componente provee la estructura (Navbar, Footer, Container)
// y controla quÃ© vista (Componente) se muestra
const Layout = ({ children, setView = () => {} }) => {

    // FunciÃ³n para simular el total del carrito (simplificado)
    const getCartTotal = () => {
        try {
            const carrito = JSON.parse(localStorage.getItem('carrito')) || [];
            const total = carrito.reduce((sum, item) => sum + (Number(item.precio) || 0), 0);
            return total.toLocaleString('es-CL'); 
        } catch (e) {
            return '0';
        }
    };

    return (
        <>
            <Navbar bg="dark" variant="dark" expand="lg" className="sticky-top">
                <Container>
                    <Navbar.Brand onClick={() => setView('home')} style={{ cursor: 'pointer' }}>
                        Coffee Shop â˜•
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="me-auto">
                            <Nav.Link onClick={() => setView('home')}>Home</Nav.Link>
                            <Nav.Link onClick={() => setView('registro')}>Registro</Nav.Link>
                            <Nav.Link onClick={() => setView('admin')}>Admin</Nav.Link>
                        </Nav>
                        <Button variant="success" className="ms-3" onClick={() => setView('carrito')}>
                            ðŸ›’ Carrito ${getCartTotal()}
                        </Button>
                        <Nav>
                            <Nav.Link onClick={() => setView('login')} className="text-white">Iniciar SesiÃ³n</Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>

            <Container style={{ minHeight: '80vh' }}>
                {children}
            </Container>

            <footer className="bg-light text-center py-3 mt-5 border-top">
                <p>&copy; {new Date().getFullYear()} Coffee Shop. Desarrollo Frontend React.</p>
            </footer>
        </>
    );
};

export default Layout;
