import React from 'react';
import { Tabs, Tab, Container } from 'react-bootstrap';
import UserTable from './UserTable'; // Componente de tabla de usuarios que creamos
// import ProductTable from './ProductTable'; // Lo crearíamos después

// Vista principal del Panel de Administración
const AdminDashboard = () => {
    
    return (
        <Container className="my-5">
            <h1 className="text-center">Panel de Administración</h1>
            <p className="text-center text-muted">Gestión Centralizada (Reutilizando Componentes)</p>

            {/* Tabs de React-Bootstrap para la navegación del panel */}
            <Tabs defaultActiveKey="usuarios" id="admin-tabs" className="mb-3">
                
                {/* Pestaña de Usuarios (Usando el componente migrado) */}
                <Tab eventKey="usuarios" title="Usuarios">
                    <h3 className="mt-3">Gestión de Clientes y Roles</h3>
                    <UserTable />
                    {/* Aquí se mostraría la lista completa con los botones de Editar/Eliminar */}
                </Tab>

                {/* Pestaña de Productos */}
                <Tab eventKey="productos" title="Productos">
                    <h3 className="mt-3">Gestión de Inventario</h3>
                    {/* Si tuviéramos ProductTable, iría aquí */}
                    <p>Aquí se mostraría la tabla de productos (CRUD).</p>
                    {/* Por ahora, usaremos el mismo UserTable como ejemplo de cómo se vería */}
                    {/* <ProductTable /> */}
                </Tab>

                {/* Pestaña de Informes (según diagrama) */}
                <Tab eventKey="reportes" title="Reportes" disabled>
                    <p className="text-center my-4">Vista futura: Órdenes, Boletas y Reportes[cite: 1039].</p>
                </Tab>
            </Tabs>
        </Container>
    );
};

export default AdminDashboard;
