import React from 'react';
import { Table, Col } from "react-bootstrap";
import DynamicTHead from "./DynamicTHead";
import DynamicTBody from "./DynamicTBody";

// El componente principal que recibe los datos y el título
const DynamicDataGrid = ({ title, data }) => {
  
  // Si no hay datos, no podemos obtener los campos (genera error)
  if (!data || data.length === 0) {
    return (
      <Col xl={6} sm={12} className="my-3">
        <h2>{title}</h2>
        <p className="text-danger">No hay datos disponibles para mostrar la tabla.</p>
      </Col>
    );
  }
  
  // Obtiene los nombres de las columnas usando las claves del primer objeto
  const fields = Object.keys(data[0]);

  return (
    <Col xl={6} sm={12} className="my-3">
      <h2>{title}</h2>
      <div className="border rounded" style={{ maxHeight: '400px', overflowY: 'auto' }}>
        <Table striped bordered hover className="mb-0">
          {/* Llama a los componentes de soporte */}
          <DynamicTHead fields={fields} className="sticky-top bg-light" />
          <DynamicTBody data={data} fields={fields} />
        </Table>
      </div>
    </Col>
  );
};

// Exportamos este componente para usarlo en UserTable.js
export default DynamicDataGrid;
