import React, { useState, useEffect } from 'react';
import { Button, Table, Alert, Row, Col } from 'react-bootstrap';
import { getProductsLS } from '../data/data';

// Función para obtener la lista de productos del carrito desde LS
const getCartItems = () => {
    return JSON.parse(localStorage.getItem('carrito')) || [];
};

// Componente principal del Carrito
const Cart = () => {
    // 1. useState: Guarda el estado del carrito (items en el array)
    const [cartItems, setCartItems] = useState(getCartItems());
    
    // 2. useState: Fuerza la actualización de la vista (para reflejar cambios en el total)
    const [total, setTotal] = useState(0);

    // 3. useEffect: Se ejecuta al cargar y cada vez que cartItems cambia (para recalcular el total)
    useEffect(() => {
        // Recalcular el total y agrupar ítems
        let total = 0;
        
        // Agrupación de ítems (como lo hacías en JS puro)
        const groupedItems = {};
        cartItems.forEach(item => {
            if (!groupedItems[item.id]) {
                groupedItems[item.id] = { ...item, cantidad: 0 };
            }
            groupedItems[item.id].cantidad++;
            total += item.precio;
        });

        setTotal(total);
        // Opcional: Podríamos guardar los ítems agrupados en otro estado si la tabla los necesita.
        // Por ahora, usamos cartItems directamente.

    }, [cartItems]); // DEPENDENCIA: Se ejecuta si el contenido del carrito cambia

    // ----------------------------------------------------------------
    // LÓGICA DE MANIPULACIÓN DEL CARRITO
    // ----------------------------------------------------------------

    // Función para actualizar LocalStorage y el estado de React
    const updateCart = (newCart) => {
        localStorage.setItem('carrito', JSON.stringify(newCart));
        setCartItems(newCart); // Actualiza el estado para forzar el re-renderizado
    };

    // Elimina una sola unidad de un producto (Decrementa)
    const decreaseQuantity = (id) => {
        let currentCart = getCartItems();
        // Encuentra el índice del primer elemento con ese ID
        const index = currentCart.findIndex(item => item.id === id);
        if (index !== -1) {
            currentCart.splice(index, 1); // Elimina 1 elemento en esa posición
            updateCart(currentCart);
        }
    };

    // Añade una unidad de un producto (Incrementa)
    const increaseQuantity = (id) => {
        const productData = getProductsLS();
        const productToAdd = productData.find(p => p.id === id);

        if (productToAdd) {
            let currentCart = getCartItems();
            currentCart.push(productToAdd);
            updateCart(currentCart);
        }
    };

    // Elimina todas las unidades de un producto
    const removeAllItems = (id) => {
        let currentCart = getCartItems();
        const newCart = currentCart.filter(item => item.id !== id);
        updateCart(newCart);
    };

    // Simulación de pago
    const handleCheckout = () => {
        alert(`Total a pagar: $${total.toLocaleString('es-CL')}. ¡Simulación de pago exitosa!`);
        updateCart([]); // Vacía el carrito después del pago
    };

    // Agrupar los ítems para mostrarlos en la tabla (similar a la lógica en useEffect)
    const displayItems = cartItems.reduce((acc, item) => {
        if (!acc[item.id]) {
            acc[item.id] = { ...item, cantidad: 0 };
        }
        acc[item.id].cantidad++;
        return acc;
    }, {});
    
    // ----------------------------------------------------------------
    // RENDERIZADO DEL CARRITO
    // ----------------------------------------------------------------
    
    if (cartItems.length === 0) {
        return (
            <Alert variant="info" className="my-5 text-center">
                Tu carrito está vacío. ¡Añade productos para empezar a comprar!
            </Alert>
        );
    }
    
    return (
        <div className="my-5">
            <h1 className="mb-4">Mi Carrito de Compras</h1>
            <Table striped bordered hover responsive>
                <thead>
                    <tr>
                        <th>Producto</th>
                        <th>Precio Unitario</th>
                        <th>Cantidad</th>
                        <th>Subtotal</th>
                        <th>Acción</th>
                    </tr>
                </thead>
                <tbody>
                    {Object.values(displayItems).map(item => (
                        <tr key={item.id}>
                            <td>{item.nombre}</td>
                            <td>${item.precio.toLocaleString('es-CL')}</td>
                            <td>
                                {/* Controles de Cantidad */}
                                <Button size="sm" variant="outline-primary" onClick={() => decreaseQuantity(item.id)} disabled={item.cantidad <= 1}>-</Button>
                                <span className="mx-2">{item.cantidad}</span>
                                <Button size="sm" variant="outline-primary" onClick={() => increaseQuantity(item.id)}>+</Button>
                            </td>
                            <td>${(item.precio * item.cantidad).toLocaleString('es-CL')}</td>
                            <td>
                                {/* Botón para eliminar todo el producto */}
                                <Button variant="danger" size="sm" onClick={() => removeAllItems(item.id)}>Eliminar</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
            
            {/* Resumen y Botón de Pago */}
            <Row className="justify-content-end mt-4">
                <Col md={4} className="text-right">
                    <h3 className="fw-bold">Total a pagar: ${total.toLocaleString('es-CL')}</h3>
                    <Button 
                        variant="success" 
                        size="lg" 
                        className="w-100 mt-3" 
                        onClick={handleCheckout}
                    >
                        Pagar Ahora
                    </Button>
                </Col>
            </Row>
        </div>
    );
};

export default Cart;
