import React, { useState } from 'react';
import Layout from './components/Layout';
import './App.css';
import ProductList from "./components/ProductList";
import RegistroForm from "./components/RegistroForm"; 
import AdminDashboard from "./components/AdminDashboard";
import Cart from "./components/Cart"; // Asegúrate de que este componente exista

function App() {
  const [currentView, setCurrentView] = useState('home');

  // Lógica para determinar qué componente mostrar
  const renderView = () => {
    switch (currentView) {
      case 'home':
        return <ProductList />;
      case 'registro':
        return <RegistroForm />;
      case 'admin':
        return <AdminDashboard />;
      case 'carrito':
        return <Cart />;
      case 'login':
        return <h2>Página de Inicio de Sesión</h2>; // Placeholder
      default:
        return <ProductList />;
    }
  };

  return (
    // La estructura DEBE ser: <Layout> Contenido </Layout>
    <Layout setView={setCurrentView} currentView={currentView}>
      {/* Aquí solo devolvemos el resultado de la función renderView */}
      {renderView()} 
    </Layout>
  );
}

export default App;
