// ...existing code...
import React from 'react';

// Layout simple sin react-bootstrap para evitar errores por dependencias
const Layout = ({ children, setView = () => {} }) => {
  const getCartTotal = () => {
    try {
      const carrito = JSON.parse(localStorage.getItem('carrito')) || [];
      const total = carrito.reduce((s, it) => s + (Number(it.precio) || 0), 0);
      return total.toLocaleString('es-CL');
    } catch {
      return '0';
    }
  };

  return (
    <>
      <header style={{ background: '#222', color: '#fff', padding: 12 }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div style={{ cursor: 'pointer', fontWeight: 'bold' }} onClick={() => setView('home')}>Coffee Shop â˜•</div>
          <nav>
            <button onClick={() => setView('home')} style={{ marginRight: 8 }}>Home</button>
            <button onClick={() => setView('registro')} style={{ marginRight: 8 }}>Registro</button>
            <button onClick={() => setView('admin')} style={{ marginRight: 8 }}>Admin</button>
            <button onClick={() => setView('carrito')}>ðŸ›’ ${getCartTotal()}</button>
          </nav>
        </div>
      </header>

      <main style={{ minHeight: '80vh', padding: 16 }}>
        {children}
      </main>

      <footer style={{ textAlign: 'center', padding: 12, borderTop: '1px solid #ddd' }}>
        &copy; {new Date().getFullYear()} Coffee Shop
      </footer>
    </>
  );
};

export default Layout;
